import java.io.ByteArrayOutputStream;
import java.lang.reflect.Array;
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Date;
import java.util.List;

public class Constant {
    public static String BASE64="data:image/png;base64,";
    public final static String ACTIVE_FILTER_COLOR_STRING = "#959595";
    public static Address userAddress = null;

    public  final static String INACTIVE_FILTER_COLOR_STRING = "#FFFFFF";
    public final static int MAX_ADDRESS=10;
    public static List<Cart> cartArray = new ArrayList<>();
    public static List<Cart> payArray = new ArrayList<>();
    public static User userCurrent ;
    public static List<Product> products= new ArrayList<>();
    public static List<String> status= Arrays.asList("Đang xử lý","Yêu cầu huỷ","Đang giao","Đã giao","Đã huỷ");

    public  static String getImage(int id){
        String image = null;
        for (int i=0;i<products.size();i++){
            if (products.get(i).getProductID().equals(id))
                image = products.get(i).getImage();
        }
        return image;
    }
    public  static String getName(int id){
        String name = null;
        for (int i=0;i<products.size();i++){
            if (products.get(i).getProductID().equals(id))
                name = products.get(i).getProduct_name();
        }
        return name;
    }
    public static Date formatDate(Date date) throws ParseException {
        SimpleDateFormat inputFormat = new SimpleDateFormat("EEE MMM dd HH:mm:ss zzz yyyy");
        Date dateOut = inputFormat.parse(date.toString());
        SimpleDateFormat outputFormat = new SimpleDateFormat("yyyy-MM-dd");
        Date output = outputFormat.parse(dateOut.toString());
        return output;
    }


    public static String encodeToBase64(String imagePath) {
        Bitmap bm = BitmapFactory.decodeFile(imagePath);
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        bm.compress(Bitmap.CompressFormat.JPEG, 100, byteArrayOutputStream);
        byte[] byteArray = byteArrayOutputStream.toByteArray();
        String str ="data:image/png;base64,"+Base64.encodeToString(byteArray, Base64.DEFAULT);
        String escapedStr = str.replaceAll("\n", "");
//        System.out.println("encodeToBase64: "+str );
        return escapedStr;
    }
    public static Bitmap loadImage(String image){
        byte[] bytes= Base64.decode(image.substring(image.lastIndexOf(",")+1),Base64.DEFAULT)  ;
        Bitmap bitmap = BitmapFactory.decodeByteArray(bytes,0,bytes.length);
        return bitmap;
    }
    public static Date convertStringToDate(String dateString) {
        SimpleDateFormat formatter = new SimpleDateFormat("yyyy-MM-dd");
        try {
            Date date = formatter.parse(dateString);
            return date;
        } catch (ParseException e) {
            e.printStackTrace();
            return null;
        }
    }
}
